import Layout from "../Layout";
import ReactPageScroller from "react-page-scroller";
import React, { Component } from "react";
import Nav from "../Nav";
import Foot from "../Foot";

function Rules() {
  return (
    <div class="w-full lg:h-screen flex justify-center">
      <div class="px-14 lg:py-14 py-64  bg-lime-400 font-Plex w-full 2xl:container flex flex-col justify-center">
        <p class="underline lg:text-7xl pb-8 text-8xl">RULES</p>
        <ol class="text-4xl lg:text-base leading-loose lg:leading-normal">
          <li class="pb-4 font-Lora">
            ↳ Participants must be between 13-18 years of age at the time of
            submission.{" "}
          </li>
          <li class="pb-4 font-Lora">
            ↳ The deadline for submission is September 30th 23:59 UTC +08:00.
          </li>
          <li class="pb-4 font-Lora">
            ↳ Participants may enter individually or in teams of up to 4.
          </li>
          <li class="pb-4 font-Lora">
            ↳ Participants shortlisted for the elevator pitch should prepare a
            PowerPoint presentation that summarises their report. This must not
            exceed 10 minutes.
          </li>
          <li class="pb-4 font-Lora">
            ↳ This is an open competition, meaning that participants may consult
            materials outside of the case to aid them in their analysis. Such
            materials may include online courses, textbooks, online resources,
            or Excel templates. If you do use any outside resources, you must
            reference them at some point in your deliverables. You are not
            permitted to have someone outside your team directly help you put
            together your submission. Your deliverables should be a product of
            your personal analysis and work, and cannot be plagiarised or be
            someone elses work. Submissions that breach this rule will not be
            accepted.
          </li>
        </ol>
      </div>
    </div>
  );
}

function JudingCriteria() {
  return (
    <div class="w-full h-screen flex justify-center">
      <div class="px-14 lg:py-14 py-64  bg-rose-400 font-Plex w-full 2xl:container flex flex-col justify-center">
        <p class="underline lg:text-7xl pb-8 text-8xl">JUDGING CRITERIA</p>
        <ol class="text-4xl lg:text-base leading-loose lg:leading-normal">
          <li class="pb-4 font-Lora">
            ↳ Investment rationale and strategy: clear investment rationale and
            strategy is supported by case materials.
          </li>
          <li class="pb-4 font-Lora">
            ↳ Drivers and assumptions: clarity and quality of model, including
            assumptions, logic, market sizing, and drivers.
          </li>
          <li class="pb-4 font-Lora">
            ↳ Formatting: Accurate use of best practices and easy to follow
            aesthetic model/pitchbook. <br></br>
            <span class="pl-4 font-extrabold">
              ↳ Are you utilising a clear and accurate excel format?
            </span>
          </li>
          <li class="pb-4 font-Lora">
            ↳ Investment presentation slide deck: Is effectively structured,
            showcasing key takeaways and high quality recommendations and
            includes the presentation of outputs namely charts and graphs
          </li>
          <li class="pb-4 font-Lora">
            ↳ Presentation recording: Is clear and dynamic, communicates the
            main insights from the presentation deck and model in a professional
            manner.
          </li>
        </ol>
      </div>
    </div>
  );
}

function Registration() {
  return (
    <div class="w-full h-screen flex justify-center">
      <div class="px-14 lg:py-14 py-64  bg-indigo-400 font-Plex w-full 2xl:container flex flex-col justify-center">
        <p class="underline lg:text-7xl pb-8 text-8xl">REGISTRATION GUIDE</p>
        <ol class="text-4xl lg:text-base leading-loose lg:leading-normal">
          <h4 class="font-Lora">
            Participants register individually or in teams of 2-4
          </h4>
          <ol class="pl-4 pb-4">
            <li class="font-Lora">
              ↳ A team leader should be elected to fill out the registration
              form on behalf of other members
            </li>
            <li class="font-Lora">
              ↳ Members cannot be added after the initial registration form is
              complete
            </li>
          </ol>
          <h4 class="font-Lora">To register, you will need</h4>
          <ol class="pl-4 pb-4">
            <li class="font-Lora">
              ↳ Participants’ full details (no school emails)
              <li class="font-Lora">↳ Parental consent if you are under 18</li>
            </li>
          </ol>
          <h4 class="font-Lora">Instructions</h4>
          <ol class="pl-4">
            <li class="font-Lora">↳ (Optional) Read the FAQs</li>
            <li class="font-Lora">
              ↳ Click the Register button on the top right corner of the website
            </li>
            <li class="font-Lora">↳ Fill out the form</li>
            <li class="font-Lora">↳ Registration complete</li>
            <li class="font-Lora">
              {" "}
              ↳ Wait for your case brief on September 10th.
            </li>
          </ol>
        </ol>
      </div>
    </div>
  );
}

export default class Guidelines extends Component {
  constructor(props) {
    super(props);
    this.state = { currentPage: null, width: 0 };
    this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
  }

  handlePageChange = (number) => {
    this.setState({ currentPage: number });
  };

  componentDidMount() {
    this.updateWindowDimensions();
    window.addEventListener("resize", this.updateWindowDimensions);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.updateWindowDimensions);
  }

  updateWindowDimensions() {
    this.setState({ width: window.outerWidth });
  }

  render() {
    if (this.state.width <= 1024) {
      return (
        <div class="flex flex-col">
          <Nav />
          <Rules />
          <JudingCriteria />
          <Registration />
          <Foot />
        </div>
      );
    } else {
      return (
        <Layout>
          <ReactPageScroller pageOnChange={this.handlePageChange}>
            <Rules />
            <JudingCriteria />
            <Registration />
          </ReactPageScroller>
        </Layout>
      );
    }
  }
}
